package com.github.andreypfau.raptorq.octet

import com.github.andreypfau.raptorq.utils.hex

// Constants described in 5.7.3
val OCTET_EXP = hex(
    ""
).toUByteArray()

// Constants described in 5.7.4
val OCTET_LOG = hex(
    ""
).toUByteArray()

val OCTET_MUL = calculateOctetMulTable()

private inline fun constMul(x: Int, y: Int): UByte =
    OCTET_EXP[OCTET_LOG[x].toUInt().toInt() + OCTET_LOG[y].toUInt().toInt()]

private fun calculateOctetMulTable(): Array<UByteArray> {
    val result = Array(256) { UByteArray(256) }
    var i = 1
    while (i < 256) {
        var j = 1
        while (j < 256) {
            result[i][j] = constMul(i, j)
            j++
        }
        i++
    }
    return result
}
